"use strict";

var mySwiper = new Swiper ('.swiper-container', {
   // Optional parameters
   updateOnWindowResize: true,
   loop: true,
   spaceBetween: 300,
   grabCursor: true,
   // Navigation arrows
   navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
   },
   flipEffect: {
      rotate: 30,
      slideShadows: false,
   },
   // Disable preloading of all images
   preloadImages: false,
   // Enable lazy loading
   lazy: true
})

var swiper = new Swiper('.swiper-events', {
   slidesPerView: 4,
   spaceBetween: 30,

   breakpoints: {
      // when window width is >= 320px
      320: {
         slidesPerView: 1,
         spaceBetween: 20
      },
      // when window width is >= 480px
      480: {
         slidesPerView: 2,
         spaceBetween: 30
      },
      // when window width is >= 640px
      720: {
         slidesPerView: 3,
         spaceBetween: 40
      }
   },
   // Disable preloading of all images
   preloadImages: false,
   // Enable lazy loading
   lazy: true
})

var swiper = new Swiper('.swiper-partners', {
   direction: 'horizontal',
   
   breakpoints: {
      // when window width is >= 320px
      320: {
         slidesPerView: 2,
         spaceBetween: 30
      },
      // when window width is >= 480px
      480: {
         slidesPerView: 3,
         spaceBetween: 30
      },
      // when window width is >= 640px
      640: {
         slidesPerView: 4,
         spaceBetween: 40
      }
   },
   // Disable preloading of all images
   preloadImages: false,
   // Enable lazy loading
   lazy: true
})

$('.mobile-menu > .burger__button').on('click', function(e) {
   e.preventDefault();
   $(this).toggleClass('burger_button__active');
   $('.nav-mobile').toggleClass('nav-mobile__active');
   $('.nav-mobile a').on('click', function() {
      if ($('.nav-mobile').hasClass('nav-mobile__active')) {
         $('.nav-mobile').removeClass('nav-mobile__active');
      }
   })
})
      
