"use strict";

$('nav .burger__button').on('click', function(e) {
   e.preventDefault();
   $(this).toggleClass('burger__button__active');
   $('.mobile-menu').toggleClass('mobile__menu__active');
   $(".mobile-menu a").on("click",function(){
		if($(".mobile-menu").hasClass("mobile__menu__active")) {
			$(".mobile-menu").removeClass("mobile__menu__active");
			$(".burger__button").removeClass("burger__button__active")
		};
	})
});